																						//SKILLBARS
//PHOTOSHOP
$(document).ready(function(){

	$(window).on("scroll", function(){
		

		if($(window).scrollTop() >= 1278){
			document.querySelector(".skillbar-bar").style.width = "79%"
		}
	})
})
